export default (function ListFormat(locales) {
    return (value) => {
        return {
            locales,
            options: {
                type: 'conjunction',
                style: 'long',
            },
            value,
            conjunction() {
                this.options.type = 'conjunction';
                return this;
            },
            disjunction() {
                this.options.type = 'disjunction';
                return this;
            },
            unit() {
                this.options.type = 'unit';
                return this;
            },
            long() {
                this.options.style = 'long';
                return this;
            },
            short() {
                this.options.style = 'short';
                return this;
            },
            narrow() {
                this.options.style = 'narrow';
                return this;
            },
            get result() {
                const formatter = new Intl.ListFormat(this.locales, this.options);
                return formatter.format(this.value);
            },
        };
    };
});
