import { I18nLite } from '@eartharoid/i18n';
export default class I18n extends I18nLite {
    constructor(options) {
        super(options);
    }
    load(...modules) {
        const locale_id = modules[0].locale_id;
        const messages = [].concat(...modules.map(mod => mod.json));
        return this.loadParsed(locale_id, messages);
    }
}
